import React, { useState } from 'react';

const Contact = () => {
  const services = [
    'Yoga & Meditation',
    'Panchakarma Treatment',
    'Therapeutic Massage',
    'Ayurvedic Consultation',
    'Herbal Medicine'
  ];

  const durationOptions = [
    '3 days',
    '7 days',
    '14 days',
    '21 days',
    '28 days',
    'Custom Duration'
  ];

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    service: 'Yoga & Meditation',
    duration: '7 days',
    message: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Google Apps Script URL
      const googleSheetURL = 'https://script.google.com/macros/s/AKfycbxyr6cAXK0SqN-y7dl8tRkz9sDjWxl5Vb18hJ-MqxWBJd2_PAYSaolcQLOclOIRrbIJHg/exec';

      const formPayload = new FormData();
      formPayload.append('name', formData.name);
      formPayload.append('email', formData.email);
      formPayload.append('phone', formData.phone);
      formPayload.append('service', formData.service);
      formPayload.append('duration', formData.duration);
      formPayload.append('message', formData.message);
      formPayload.append('timestamp', new Date().toLocaleString());

      const response = await fetch(googleSheetURL, {
        method: 'POST',
        body: formPayload,
        mode: 'no-cors'
      });

