import React, { useState } from 'react';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    message: '',
    duration: '7 days'
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Replace this with your Google Apps Script URL
      const googleSheetURL = 'https://script.google.com/macros/s/AKfycbzbkGMWCdE4qcBo4wo1fb6qiKVThp27nmuhCFYiI6pgQ1--UfBDs2qOpxSQ4_OvVLR6sg/exec';

      const response = await fetch(googleSheetURL, {
        method: 'POST',
        body: JSON.stringify({
          name: formData.name,
          email: formData.email,
          phone: formData.phone,
          duration: formData.duration,
          message: formData.message,
          timestamp: new Date().toLocaleString()
        })
      });

      if (response.ok) {
        alert('Thank you for your interest! We will contact you soon.');
        setFormData({ name: '', email: '', phone: '', message: '', duration: '7 days' });
      } else {
        alert('Message sent but there was an issue saving to database. Please try again.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Thank you for your interest! We will contact you soon.');
      setFormData({ name: '', email: '', phone: '', message: '', duration: '7 days' });
    }
  };

  return (
    <section id="contact" style={{
      padding: '5rem 2rem',
      background: 'linear-gradient(135deg, #F5F9F0 0%, #ffffff 100%)',
      position: 'relative'
    }}>
      <div style={{
        maxWidth: '1200px',
        margin: '0 auto'
      }}>
        <div style={{
          textAlign: 'center',
          marginBottom: '3rem'
        }}>
          <h2 style={{
            fontSize: '2.5rem',
            fontWeight: '700',
            color: '#2d3748',
            marginBottom: '1rem'
